mapper.select:
	SELECT notice_board_code,  member_id, notice_board_title, notice_board_content, notice_board_reg_date
FROM notice_board
	  WHERE notice_board_code = #{noticeBoardCode};

SELECT CAST(SUBSTRING_INDEX(nb.notice_board_code, '_', -1) AS SIGNED) AS notice_board_code, nb.member_id, nb.notice_board_title, nb.notice_board_content, nb.notice_board_reg_date FROM notice_board AS nb
${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY notice_board_code ASC, SUBSTRING_INDEX(nb.notice_board_code, '_', 1) ASC;
